import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { ordersAPI } from '../api/orders';
import { productsAPI } from '../api/products';
import { toast } from 'react-toastify';
import { 
  ShoppingBag, 
  Package, 
  TrendingUp, 
  Users,
  Plus,
  Eye,
  BarChart3,
  Calendar,
  DollarSign,
  AlertCircle
} from 'lucide-react';
import OrderForm from '../components/orders/OrderForm';

const Dashboard = () => {
  const { user, isVendor, isSupplier } = useAuth();
  const [stats, setStats] = useState({
    totalOrders: 0,
    totalProducts: 0,
    totalRevenue: 0,
    pendingOrders: 0
  });
  const [recentOrders, setRecentOrders] = useState([]);
  const [recentProducts, setRecentProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showOrderForm, setShowOrderForm] = useState(false);
  
  // Track previous user state to detect login
  const prevUserRef = useRef(null);
  const hasReloadedAfterLogin = useRef(false);

  // Auto-reload on login detection
  useEffect(() => {
    // Check if user just logged in (was null/undefined, now has value)
    const wasLoggedOut = !prevUserRef.current;
    const isNowLoggedIn = !!user;
    
    if (wasLoggedOut && isNowLoggedIn && !hasReloadedAfterLogin.current) {
      console.log('User login detected, reloading page...');
      hasReloadedAfterLogin.current = true;
      
      // Small delay to ensure auth context is fully updated
      setTimeout(() => {
        window.location.reload();
      }, 100);
    }
    
    // Update previous user reference
    prevUserRef.current = user;
  }, [user]);

  // Alternative approach: Check localStorage for login flag
  useEffect(() => {
    const shouldReloadAfterLogin = localStorage.getItem('shouldReloadAfterLogin');
    if (shouldReloadAfterLogin === 'true' && user) {
      localStorage.removeItem('shouldReloadAfterLogin');
      window.location.reload();
    }
  }, [user]);

  // Force refresh on component mount
  useEffect(() => {
    // Clear any cached data first
    setStats({
      totalOrders: 0,
      totalProducts: 0,
      totalRevenue: 0,
      pendingOrders: 0
    });
    setRecentOrders([]);
    setRecentProducts([]);
    
    // Add a small delay to ensure state is cleared, then fetch fresh data
    const timeoutId = setTimeout(() => {
      fetchDashboardData(true); // Pass true to force refresh
    }, 100);

    return () => clearTimeout(timeoutId);
  }, []); // Empty dependency array ensures this runs only on mount

  // Also listen for auth changes to refresh data
  useEffect(() => {
    if (user && !hasReloadedAfterLogin.current) {
      fetchDashboardData(true);
    }
  }, [user?._id]); // Refresh when user changes

  const fetchDashboardData = async (forceRefresh = false) => {
    try {
      setLoading(true);
      
      // Add cache-busting parameter for force refresh
      const cacheParam = forceRefresh ? `?_t=${Date.now()}` : '';
      
      // Fetch orders with cache busting
      let ordersResponse;
      if (isVendor()) {
        ordersResponse = await ordersAPI.getVendorOrders(cacheParam);
      } else if (isSupplier()) {
        ordersResponse = await ordersAPI.getSupplierOrders(cacheParam);
      }
      
      const orders = ordersResponse?.data || [];
      setRecentOrders(orders.slice(0, 5)); // Get recent 5 orders
      
      // Calculate stats from orders
      const totalRevenue = orders.reduce((sum, order) => sum + order.totalAmount, 0);
      const pendingOrders = orders.filter(order => order.status === 'pending').length;
      
      setStats(prev => ({
        ...prev,
        totalOrders: orders.length,
        totalRevenue,
        pendingOrders
      }));

      // Fetch products for suppliers
      if (isSupplier()) {
        const productsResponse = await productsAPI.getProducts(cacheParam);
        const products = productsResponse?.data || [];
        const userProducts = products.filter(product => 
          product.supplier?._id === user._id || product.supplier === user._id
        );
        setRecentProducts(userProducts.slice(0, 5));
        setStats(prev => ({
          ...prev,
          totalProducts: userProducts.length
        }));
      } else if (isVendor()) {
        // For vendors, show available products
        const productsResponse = await productsAPI.getProducts(cacheParam);
        const products = productsResponse?.data || [];
        setRecentProducts(products.slice(0, 5));
        setStats(prev => ({
          ...prev,
          totalProducts: products.length
        }));
      }
      
    } catch (error) {
      toast.error('Failed to fetch dashboard data');
      console.error('Dashboard error:', error);
    } finally {
      setLoading(false);
    }
  };

  // Add a manual refresh function
  const handleRefresh = () => {
    fetchDashboardData(true);
  };

  // Manual reload function
  const handleManualReload = () => {
    window.location.reload();
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      pending: 'badge-warning',
      processing: 'badge-info',
      shipped: 'badge-secondary',
      delivered: 'badge-success',
      cancelled: 'badge-danger',
    };
    return statusClasses[status] || 'badge-secondary';
  };

  if (loading) {
    return (
      <div className="container">
        <div className="flex items-center justify-center min-h-screen">
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="main-content">
        {/* Welcome Header with Refresh Button */}
        <div className="mb-8">
          <div className="flex justify-between items-start">
            <div>
              <h1 className="text-3xl font-bold mb-2">
                Welcome back, {user?.name}!
              </h1>
              <p className="text-gray-600">
                {isVendor() 
                  ? 'Manage your orders and discover new suppliers' 
                  : 'Track your sales and manage your inventory'
                }
              </p>
            </div>
            <div className="flex gap-2">
              <button 
                onClick={handleRefresh}
                className="btn btn-outline btn-sm"
                disabled={loading}
              >
                {loading ? 'Refreshing...' : 'Refresh Data'}
              </button>
              <button 
                onClick={handleManualReload}
                className="btn btn-secondary btn-sm"
                title="Reload entire page"
              >
                Reload Page
              </button>
            </div>
          </div>
        </div>

        {/* Stats Grid */}
        <div className="stats-grid mb-8">
          <div className="stat-card">
            <div className="flex items-center justify-between">
              <div>
                <div className="stat-value">{stats.totalOrders}</div>
                <div className="stat-label">
                  {isVendor() ? 'Orders Placed' : 'Orders Received'}
                </div>
              </div>
              <ShoppingBag size={32} className="text-blue-500" />
            </div>
          </div>

          <div className="stat-card">
            <div className="flex items-center justify-between">
              <div>
                <div className="stat-value">{stats.totalProducts}</div>
                <div className="stat-label">
                  {isVendor() ? 'Available Products' : 'Products Listed'}
                </div>
              </div>
              <Package size={32} className="text-green-500" />
            </div>
          </div>

          <div className="stat-card">
            <div className="flex items-center justify-between">
              <div>
                <div className="stat-value">₹{stats.totalRevenue.toLocaleString()}</div>
                <div className="stat-label">
                  {isVendor() ? 'Total Spent' : 'Total Revenue'}
                </div>
              </div>
              <TrendingUp size={32} className="text-purple-500" />
            </div>
          </div>

          <div className="stat-card">
            <div className="flex items-center justify-between">
              <div>
                <div className="stat-value">{stats.pendingOrders}</div>
                <div className="stat-label">Pending Orders</div>
              </div>
              <AlertCircle size={32} className="text-orange-500" />
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="card mb-8">
          <div className="card-header">
            <h3 className="card-title">Quick Actions</h3>
          </div>
          <div className="card-body">
            <div className="flex flex-wrap gap-4">
              {isVendor() && (
                <>
                  <button
                    onClick={() => setShowOrderForm(true)}
                    className="btn btn-primary"
                  >
                    <Plus size={20} className="mr-2" />
                    Place New Order
                  </button>
                  <Link to="/products" className="btn btn-secondary">
                    <Package size={20} className="mr-2" />
                    Browse Products
                  </Link>
                  <Link to="/vendor-orders" className="btn btn-outline">
                    <BarChart3 size={20} className="mr-2" />
                    View All Orders
                  </Link>
                </>
              )}
              
              {isSupplier() && (
                <>
                  <Link to="/products" className="btn btn-primary">
                    <Plus size={20} className="mr-2" />
                    Add Product
                  </Link>
                  <Link to="/supplier-orders" className="btn btn-secondary">
                    <BarChart3 size={20} className="mr-2" />
                    Manage Orders
                  </Link>
                  <Link to="/products" className="btn btn-outline">
                    <Package size={20} className="mr-2" />
                    View Inventory
                  </Link>
                </>
              )}
            </div>
          </div>
        </div>

        {/* Recent Activity */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Recent Orders */}
          <div className="card">
            <div className="card-header">
              <div className="flex justify-between items-center">
                <h3 className="card-title">Recent Orders</h3>
                <Link 
                  to={isVendor() ? "/vendor-orders" : "/supplier-orders"} 
                  className="btn btn-outline btn-sm"
                >
                  <Eye size={16} className="mr-2" />
                  View All
                </Link>
              </div>
            </div>
            <div className="card-body">
              {recentOrders.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <ShoppingBag size={48} className="mx-auto mb-4 opacity-50" />
                  <p>No orders yet</p>
                  {isVendor() && (
                    <button
                      onClick={() => setShowOrderForm(true)}
                      className="btn btn-primary btn-sm mt-4"
                    >
                      Place Your First Order
                    </button>
                  )}
                </div>
              ) : (
                <div className="space-y-4">
                  {recentOrders.map((order) => (
                    <div key={order._id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div>
                        <div className="font-medium">
                          Order #{order._id.slice(-8)}
                        </div>
                        <div className="text-sm text-gray-500 flex items-center gap-4">
                          <span className="flex items-center gap-1">
                            <Calendar size={14} />
                            {new Date(order.createdAt).toLocaleDateString()}
                          </span>
                          <span className="flex items-center gap-1">
                            <DollarSign size={14} />
                            ₹{order.totalAmount}
                          </span>
                        </div>
                      </div>
                      <span className={`badge ${getStatusBadge(order.status)}`}>
                        {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                      </span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Recent Products */}
          <div className="card">
            <div className="card-header">
              <div className="flex justify-between items-center">
                <h3 className="card-title">
                  {isVendor() ? 'Available Products' : 'Your Products'}
                </h3>
                <Link to="/products" className="btn btn-outline btn-sm">
                  <Eye size={16} className="mr-2" />
                  View All
                </Link>
              </div>
            </div>
            <div className="card-body">
              {recentProducts.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Package size={48} className="mx-auto mb-4 opacity-50" />
                  <p>
                    {isVendor() ? 'No products available' : 'No products listed yet'}
                  </p>
                  {isSupplier() && (
                    <Link to="/products" className="btn btn-primary btn-sm mt-4">
                      Add Your First Product
                    </Link>
                  )}
                </div>
              ) : (
                <div className="space-y-4">
                  {recentProducts.map((product) => (
                    <div key={product._id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div>
                        <div className="font-medium">{product.name}</div>
                        <div className="text-sm text-gray-500">
                          {product.category} • Stock: {product.stock}
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="font-medium text-green-600">₹{product.price}</div>
                        <div className="text-sm text-gray-500">per unit</div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Order Form Modal */}
        {showOrderForm && (
          <OrderForm 
            onClose={() => {
              setShowOrderForm(false);
              // Refresh data after closing order form
              fetchDashboardData(true);
            }} 
          />
        )}
      </div>
    </div>
  );
};

export default Dashboard;
